'use strict';

var express = require('express');
var app = express();
var server = app.listen(8080);
var initApp = require('main-app').init;

before(function() {
	console.log('starting test server');
	return initApp(app);
});

after(function() {
	console.log('closing test server');
	server.close();
});

module.exports = {
	app: app
};
